// password generation function
function generatePassword(length, useLetters, useNumbers, useSymbols) {
  // password character sets
  const letters = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ';
  const numbers = '0123456789';
  const symbols = '!@#$%^&*()-_+=[]{}|;:,.<>?';

  // checking parameters
  if (!useLetters && !useNumbers && !useSymbols) {
    console.error('You must specify at least one character type to generate a password.');
    return '';
  }

  // characters on one line
  let availableChars = '';
  if (useLetters) availableChars += letters;
  if (useNumbers) availableChars += numbers;
  if (useSymbols) availableChars += symbols;

  // password generation
  let password = '';
  for (let i = 0; i < length; i++) {
    const randomIndex = Math.floor(Math.random() * availableChars.length);
    password += availableChars[randomIndex];
  }
  return password;
}
module.exports = generatePassword;